mapper.select:
SELECT TODO_NO , TODO_TITLE , TODO_CONTENT , COMPLETE , TO_CHAR(REG_DATE, 'YYYY-MM-DD HH24:MI:SS') REG_DATE  
FROM TB_TODO
WHERE TODO_NO = #{todoNo}

mapper.update:
UPDATE TB_TODO SET
COMPLETE = #{complete}
WHERE TODO_NO = #{todoNo}

UPDATE TB_TODO SET
TODO_TITLE = #{todoTitle},
TODO_CONTENT = #{todoContent}
WHERE TODO_NO = ${todoNo}
